import React from 'react';
import { MALAYSIAN_PORTS } from '../constants/malaysianPorts';
import './VesselFinderEmbed.css';

const VesselFinderEmbed = ({ globalSelectedPort }) => {
  const portCodes = {
    'labuan': 'MYLBU001',
    'port-klang': 'MYPKG001',
    'penang': 'MYPEN001',
    'johor': 'MYTPP001',
    'kuantan': 'MYKUA001',
    'bintulu': 'MYBTU001',
    'kota-kinabalu': 'MYBKI001',
    'kuching': 'MYKCH001'
  };

  const selectedPort = MALAYSIAN_PORTS.find(p => p.id === globalSelectedPort) || MALAYSIAN_PORTS[0];
  const vesselFinderUrl = `https://www.vesselfinder.com/ports/${portCodes[selectedPort.id]}`;

  return (
    <div>
      <div style={{ 
        position: 'relative', 
        overflow: 'hidden', 
        width: '100%',
        height: '50vh',
      }}>
        <iframe
          key={selectedPort.id}
          src={vesselFinderUrl}
          style={{
            position: 'relative',
            width: '100%',
            height: '300%',
            border: 'none',
            top: '-90vh'
          }}
          title="marinelink"
        />
      </div>
    </div>
  );
};

export default VesselFinderEmbed;

src/components/TideData.js.content:
import React, { useEffect, useState } from 'react';
import { AreaChart, Area, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from 'recharts';
import tideData from '../Server/tideData.json';
import { MALAYSIAN_PORTS } from '../constants/malaysianPorts';
import "../components/TideData.css";

const TideData = ({ globalSelectedPort }) => {
  const [tides, setTides] = useState([]);
  const [lastUpdated, setLastUpdated] = useState(null);
  const selectedPort = MALAYSIAN_PORTS.find(p => p.id === globalSelectedPort) || MALAYSIAN_PORTS[0];

  useEffect(() => {
    // Directly set the tide data from the imported JSON file
    setTides(tideData.heights);
    if (tideData.timestamp) {
      setLastUpdated(new Date(tideData.timestamp));
    }
  }, []);

  const formattedTides = tides.map(tide => ({
    date: new Date(tide.date).toLocaleString(), // Format to full date and time
    height: tide.height
  }));

  const getDataAge = () => {
    if (!lastUpdated) return 'Unknown';
    const ageMs = Date.now() - lastUpdated.getTime();
    const hours = Math.floor(ageMs / 1000 / 60 / 60);
    const days = Math.floor(hours / 24);

    if (days > 0) return `${days} day${days > 1 ? 's' : ''} ago`;
    if (hours > 0) return `${hours} hour${hours > 1 ? 's' : ''} ago`;
    return 'Just now';
  };

  return (
    <div>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '1rem' }}>
        <h2>Tide Data - {selectedPort.name}</h2>
        {lastUpdated && (
          <div style={{ fontSize: '0.875rem', color: '#00f3ff' }}>
            <div style={{ marginBottom: '0.25rem' }}>
              Source: <span style={{ color: '#00ff88', fontWeight: 'bold' }}>
                {tideData.source || 'Unknown'}
              </span>
            </div>
            Last updated: {getDataAge()}
            <br />
            <span style={{ fontSize: '0.75rem', opacity: 0.7 }}>
              {lastUpdated.toLocaleString()}
            </span>
          </div>
        )}
      </div>
      {tides.length > 0 ? (
        <ResponsiveContainer width="100%" height={400}>
          <AreaChart data={formattedTides}>
            <defs>
              <linearGradient id="colorHeight" x1="0" y1="0" x2="0" y2="1">
                <stop offset="5%" stopColor="#00f" stopOpacity={0.8}/>
                <stop offset="95%" stopColor="#00f" stopOpacity={0}/>
              </linearGradient>
            </defs>
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="date" tickFormatter={(tick) => {
              const date = new Date(tick);
              return `${date.getMonth() + 1}/${date.getDate()} ${date.getHours()}:${date.getMinutes().toString().padStart(2, '0')}`;
            }} />
            <YAxis />
            <Tooltip labelFormatter={(label) => new Date(label).toLocaleString()} />
            <Area type="monotone" dataKey="height" stroke="#00f" fillOpacity={1} fill="url(#colorHeight)" />
          </AreaChart>
        </ResponsiveContainer>
      ) : (
        <p>No tide data available.</p>
      )}
    </div>
  );
};

export default TideData;

src/components/MarineNews.js.content:
// src/components/MarineNews.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import '../components/MarineNews.css';

const MarineNews = () => {
  const [news, setNews] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Using a free RSS to JSON converter for maritime news
    const fetchNews = async () => {
      try {
        // Maritime Executive RSS feed converted to JSON
        const response = await axios.get(
          'https://api.rss2json.com/v1/api.json?rss_url=https://maritime-executive.com/rss.xml'
        );

        if (response.data && response.data.items) {
          setNews(response.data.items.slice(0, 20)); // Get latest 20 articles
        }
        setLoading(false);
      } catch (error) {
        console.error('Error fetching marine news:', error);
        setLoading(false);
      }
    };

    fetchNews();
    // Refresh news every 5 minutes
    const interval = setInterval(fetchNews, 5 * 60 * 1000);
    return () => clearInterval(interval);
  }, []);

  if (loading) {
    return (
      <div className="glassmorphism-card rounded-xl p-8 text-center">
        <div className="text-cyan-400 text-xl">Loading latest maritime news...</div>
      </div>
    );
  }

  return (
    <div className="glassmorphism-card rounded-xl p-6">
      <div className="flex items-center justify-between mb-6">
        <h2 className="text-2xl font-bold neon-text">Live Maritime News</h2>
        <div className="flex items-center space-x-2">
          <div className="w-2 h-2 bg-green-400 rounded-full animate-pulse"></div>
          <span className="text-xs text-green-400 font-semibold">LIVE</span>
        </div>
      </div>

      <div className="space-y-4 max-h-[70vh] overflow-y-auto">
        {news.map((article, index) => (
          <div 
            key={index} 
            className="glassmorphism rounded-lg p-4 border border-cyan-500/30 hover:border-cyan-400/50 transition-all duration-300 hover-lift-cyber"
          >
            <a 
              href={article.link} 
              target="_blank" 
              rel="noopener noreferrer"
              className="block"
            >
              <h3 className="text-lg font-bold text-cyan-400 mb-2 hover:text-cyan-300">
                {article.title}
              </h3>
              <p className="text-sm text-cyan-300/80 mb-3 line-clamp-3">
                {article.description?.replace(/<[^>]*>/g, '')}
              </p>
              <div className="flex items-center justify-between text-xs text-cyan-400/60">
                <span>{new Date(article.pubDate).toLocaleDateString()}</span>
                <span className="text-cyan-400">Read more →</span>
              </div>
            </a>
          </div>
        ))}
      </div>

      <div className="mt-6 text-center text-xs text-cyan-300/60">
        <p>News updates every 5 minutes from Maritime Executive</p>
      </div>
    </div>
  );
};

export default MarineNews;

