src/components/MalaysianPortsStats.js.content:
import React, { useState, useEffect, useMemo } from 'react';
import { FaAnchor, FaCircle } from 'react-icons/fa';
import { MALAYSIAN_PORTS } from '../constants/malaysianPorts';

const MalaysianPortsStats = ({ globalSelectedPort }) => {
  const [stats, setStats] = useState({});

  const malaysianPorts = useMemo(() => MALAYSIAN_PORTS.map(port => ({
    ...port,
    lat: port.lat || 3.1390, // Default latitude if not provided
    lon: port.lon || 101.6869, // Default longitude if not provided
    activeVessels: 0, // Initialize with 0, will be updated by API
    incoming: 0,
    outgoing: 0,
    docked: 0,
    capacity: 0,
    alerts: 0,
    draftDepth: '0.0m',
    cargo: 'Unknown'
  })), []); // Empty dependency array ensures this runs only once

  const selectedPort = globalSelectedPort || 'labuan';

  useEffect(() => {
    // Fetch real port data from backend API
    const updateStats = async () => {
      try {
        const response = await fetch('http://0.0.0.0:3001/api/port-stats');
        const data = await response.json();

        // Only use real data, no fallbacks
        if (data && data[selectedPort] && data[selectedPort].isRealData !== false) {
          setStats(data);
        } else {
          console.log('Real AIS data not available for', selectedPort);
          setStats({});
        }
      } catch (error) {
        console.error('Error fetching port stats:', error);
        setStats({});
      }
    };

    updateStats(); // Initial fetch
    const interval = setInterval(updateStats, 15000); // Update every 15 seconds

    return () => clearInterval(interval);
  }, [selectedPort]); // Update when selected port changes

  const currentStats = stats[selectedPort] || {
    activeVessels: 0,
    incoming: 0,
    outgoing: 0,
    docked: 0,
    capacity: 0,
    alerts: 0,
    isRealData: false
  };

  // Find the selected port details for draftDepth and cargo
  const selectedPortDetails = malaysianPorts.find(port => port.id === selectedPort);
  const draftDepth = selectedPortDetails ? selectedPortDetails.draftDepth : '0.0m';
  const cargoType = selectedPortDetails ? selectedPortDetails.cargo : 'Unknown';

  return (
    <div className="glassmorphism-card rounded-xl p-6 cyber-border hover-lift-cyber">
      <div className="flex items-center justify-between mb-6">
        <div className="flex items-center">
          <div className="w-10 h-10 rounded-lg bg-gradient-to-br from-cyan-500/20 to-blue-500/20 flex items-center justify-center neon-glow mr-3">
            <FaAnchor className="w-5 h-5 text-cyan-400" />
          </div>
          <h2 className="text-xl font-bold neon-text tracking-wide">
            {selectedPortDetails ? selectedPortDetails.name : 'Malaysian Ports'}
          </h2>
        </div>
        <div className="flex items-center space-x-2">
          {currentStats.activeVessels > 0 ? (
            <>
              <FaCircle className="w-2 h-2 text-green-400 animate-pulse" />
              <span className="text-xs text-green-400 font-semibold">LIVE AIS DATA</span>
            </>
          ) : (
            <>
              <FaCircle className="w-2 h-2 text-yellow-400" />
              <span className="text-xs text-yellow-400 font-semibold">NO DATA</span>
            </>
          )}
        </div>
      </div>

      {/* Stats Grid */}
      <div className="grid grid-cols-2 gap-4 mb-4">
        <div className="glassmorphism rounded-lg p-4 border border-cyan-500/20">
          <p className="text-xs text-cyan-300/60 mb-1 uppercase tracking-wide">Active Vessels</p>
          <p className="text-2xl font-bold text-cyan-400">{currentStats.activeVessels}</p>
        </div>
        <div className="glassmorphism rounded-lg p-4 border border-cyan-500/20">
          <p className="text-xs text-cyan-300/60 mb-1 uppercase tracking-wide">Capacity</p>
          <p className="text-2xl font-bold text-green-400">{currentStats.capacity}%</p>
        </div>
        <div className="glassmorphism rounded-lg p-4 border border-cyan-500/20">
          <p className="text-xs text-cyan-300/60 mb-1 uppercase tracking-wide">Incoming</p>
          <p className="text-2xl font-bold text-blue-400">{currentStats.incoming}</p>
        </div>
        <div className="glassmorphism rounded-lg p-4 border border-cyan-500/20">
          <p className="text-xs text-cyan-300/60 mb-1 uppercase tracking-wide">Outgoing</p>
          <p className="text-2xl font-bold text-purple-400">{currentStats.outgoing}</p>
        </div>
      </div>

      {/* Additional Stats */}
      <div className="space-y-3">
        <div className="flex items-center justify-between p-3 glassmorphism rounded-lg border border-cyan-500/20">
          <span className="text-sm text-cyan-300">Docked Vessels</span>
          <span className="text-sm font-bold text-cyan-400">{currentStats.docked}</span>
        </div>
        <div className="flex items-center justify-between p-3 glassmorphism rounded-lg border border-cyan-500/20">
          <span className="text-sm text-cyan-300">Active Alerts</span>
          <span className={`text-sm font-bold ${currentStats.alerts > 0 ? 'text-red-400' : 'text-green-400'}`}>
            {currentStats.alerts}
          </span>
        </div>
        <div className="flex items-center justify-between p-3 glassmorphism rounded-lg border border-cyan-500/20">
          <span className="text-sm text-cyan-300">Draft Depth</span>
          <span className="text-sm font-bold text-yellow-400">{draftDepth}</span>
        </div>
        <div className="flex items-center justify-between p-3 glassmorphism rounded-lg border border-cyan-500/20">
          <span className="text-sm text-cyan-300">Cargo Type</span>
          <span className="text-sm font-bold text-indigo-400">{cargoType}</span>
        </div>
      </div>

      {/* Last Update */}
      <div className="mt-4 pt-4 border-t border-cyan-500/20">
        <p className="text-xs text-cyan-300/60 text-center">
          Last updated: <span className="text-cyan-400 font-semibold">{new Date().toLocaleTimeString()}</span>
        </p>
      </div>
    </div>
  );
};

export default MalaysianPortsStats;

src/components/CyberHome.js.content:
import React, { useState, useEffect } from 'react';
import { FaGlobe, FaWater, FaCloudSun, FaShip, FaAnchor } from 'react-icons/fa';
import axios from 'axios';
import StatsCards from './StatsCards';
import AIInsights from './AIInsights';
import OpenSeaMap from './OpenSeaMap';
import MalaysianPortsStats from './MalaysianPortsStats';
import { MALAYSIAN_PORTS } from '../constants/malaysianPorts';
import portDataService from '../services/portDataService';

const CyberHome = ({ globalSelectedPort }) => {
  const [weather, setWeather] = useState(null);
  const [tideData, setTideData] = useState(null);
  const [vesselCount, setVesselCount] = useState(0);
  const [vesselData, setVesselData] = useState(null);
  const [connectionStatus, setConnectionStatus] = useState('connecting');
  const [dataSource, setDataSource] = useState('unknown');
  const selectedPort = MALAYSIAN_PORTS.find(p => p.id === globalSelectedPort) || MALAYSIAN_PORTS[0];

  useEffect(() => {
    // Fetch real-time weather data for selected port
    const fetchWeather = async () => {
      try {
        const weatherApiKey = '4d5ea12f38be4e04b8c120842242507';
        const response = await fetch(
          `https://api.weatherapi.com/v1/current.json?key=${weatherApiKey}&q=${selectedPort.lat},${selectedPort.lon}`
        );

        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }

        const data = await response.json();
        setWeather(data);
      } catch (error) {
        console.error('Error fetching weather:', error);
      }
    };

    // Fetch real port-specific vessel data
    const fetchPortData = async () => {
      try {
        const response = await fetch('http://0.0.0.0:3001/api/port-stats');
        const data = await response.json();

        if (data && data[globalSelectedPort]) {
          const portData = data[globalSelectedPort];
          setVesselCount(portData.activeVessels || 0);
          setVesselData(portData);
          setConnectionStatus(portData.activeVessels > 0 ? 'connected' : 'no_data');
          setDataSource(portData.isRealData ? 'Real AIS Data' : 'No Data');
        } else {
          setVesselCount(0);
          setVesselData(null);
          setConnectionStatus('no_data');
          setDataSource('No Data');
        }
      } catch (error) {
        console.error('Error fetching port data:', error);
        setConnectionStatus('error');
      }
    };

    // Fetch real-time tide data
    const fetchTideData = async () => {
      try {
        const response = await fetch('/api/tide-data');

        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }

        const data = await response.json();
        setTideData(data);
      } catch (error) {
        console.error('Error fetching tide data:', error);
      }
    };

    // Fetch real-time vessel data from API
    const fetchVesselData = async () => {
      try {
        const response = await fetch('/api/maritime-stats');

        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }

        const maritimeStats = await response.json();
        setVesselCount(maritimeStats.activeVessels);
        setConnectionStatus(maritimeStats.connectionStatus || 'unknown');
        setDataSource(maritimeStats.dataSource || 'unknown');

        // Generate sample vessel data for display based on real count
        const mockVessels = Array.from({ length: Math.min(maritimeStats.activeVessels, 10) }, (_, i) => ({
          name: `Vessel ${i + 1}`,
          type: ['Cargo', 'Tanker', 'Container', 'Bulk Carrier'][Math.floor(Math.random() * 4)],
          status: ['In Transit', 'Docked', 'Anchored'][Math.floor(Math.random() * 3)]
        }));

        setVesselData(mockVessels);
      } catch (error) {
        console.error('Error fetching vessel data:', error);
        setVesselCount(0);
      }
    };

    fetchWeather();
    fetchTideData();
    fetchVesselData();

    // Refresh data every 30 seconds for weather, 60 seconds for tide, 15 seconds for vessels
    const weatherInterval = setInterval(fetchWeather, 30000);
    const tideInterval = setInterval(fetchTideData, 60000);
    const vesselInterval = setInterval(fetchVesselData, 15000);

    return () => {
      clearInterval(weatherInterval);
      clearInterval(tideInterval);
      clearInterval(vesselInterval);
    };
  }, [selectedPort]);

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-900 via-blue-900 to-slate-900 relative overflow-hidden">
      {/* Animated Grid Background */}
      <div className="absolute inset-0 opacity-20 pointer-events-none">
        <div className="absolute inset-0" style={{
          backgroundImage: 'linear-gradient(rgba(0, 255, 255, 0.1) 1px, transparent 1px), linear-gradient(90deg, rgba(0, 255, 255, 0.1) 1px, transparent 1px)',
          backgroundSize: '50px 50px',
          animation: 'gridScroll 20s linear infinite'
        }} />
      </div>

      <div className="container mx-auto px-6 py-8 relative z-10">
        {/* Header Section with Real-Time Status */}
        <div className="mb-8">
          <div className="flex items-center justify-between mb-6">
            <div>
              <h1 className="text-4xl font-bold bg-gradient-to-r from-cyan-400 to-blue-500 bg-clip-text text-transparent mb-2">
                Maritime Intelligence Hub
              </h1>
              <p className="text-cyan-400/70 text-lg">
                Real-Time Port Operations - {selectedPort.name}
              </p>
            </div>
            <div className={`flex items-center space-x-2 px-6 py-3 rounded-xl border ${
              connectionStatus === 'connected' 
                ? 'bg-gradient-to-r from-green-500/20 to-emerald-500/20 border-green-400/30' 
                : connectionStatus === 'stale' || connectionStatus === 'disconnected'
                ? 'bg-gradient-to-r from-yellow-500/20 to-orange-500/20 border-yellow-400/30'
                : 'bg-gradient-to-r from-red-500/20 to-red-600/20 border-red-400/30'
            }`}>
              <span className="relative flex h-3 w-3">
                {connectionStatus === 'connected' && (
                  <>
                    <span className="animate-ping absolute inline-flex h-full w-full rounded-full bg-green-400 opacity-75"></span>
                    <span className="relative inline-flex rounded-full h-3 w-3 bg-green-500"></span>
                  </>
                )}
                {(connectionStatus === 'stale' || connectionStatus === 'disconnected') && (
                  <span className="relative inline-flex rounded-full h-3 w-3 bg-yellow-500"></span>
                )}
                {connectionStatus === 'unavailable' && (
                  <span className="relative inline-flex rounded-full h-3 w-3 bg-red-500"></span>
                )}
              </span>
              <span className={`font-semibold ${
                connectionStatus === 'connected' 
                  ? 'text-green-400' 
                  : connectionStatus === 'stale' || connectionStatus === 'disconnected'
                  ? 'text-yellow-400'
                  : 'text-red-400'
              }`}>
                {connectionStatus === 'connected' && dataSource === 'aisstream.io' && 'LIVE AIS DATA'}
                {connectionStatus === 'connected' && dataSource !== 'aisstream.io' && 'LIVE DATA FEED'}
                {(connectionStatus === 'stale' || connectionStatus === 'disconnected') && 'SIMULATED DATA'}
                {connectionStatus === 'unavailable' && 'OFFLINE'}
                {connectionStatus === 'connecting' && 'CONNECTING...'}
              </span>
            </div>
          </div>

          {/* Real-Time Stats Bar */}
          <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-6">
            <div className="bg-gradient-to-br from-cyan-500/10 to-blue-500/10 rounded-xl p-4 border border-cyan-400/30">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-cyan-400/60 text-sm">Active Vessels</p>
                  <p className="text-3xl font-bold text-cyan-400">{vesselCount}</p>
                </div>
                <FaShip className="text-4xl text-cyan-400/30" />
              </div>
            </div>

            {weather && (
              <>
                <div className="bg-gradient-to-br from-orange-500/10 to-red-500/10 rounded-xl p-4 border border-orange-400/30">
                  <div className="flex items-center justify-between">
                    <div>
                      <p className="text-orange-400/60 text-sm">Temperature</p>
                      <p className="text-3xl font-bold text-orange-400">{weather.current.temp_c}°C</p>
                    </div>
                    <FaCloudSun className="text-4xl text-orange-400/30" />
                  </div>
                </div>

                <div className="bg-gradient-to-br from-blue-500/10 to-indigo-500/10 rounded-xl p-4 border border-blue-400/30">
                  <div className="flex items-center justify-between">
                    <div>
                      <p className="text-blue-400/60 text-sm">Wind Speed</p>
                      <p className="text-3xl font-bold text-blue-400">{weather.current.wind_kph} kph</p>
                    </div>
                    <FaWater className="text-4xl text-blue-400/30" />
                  </div>
                </div>
              </>
            )}

            {tideData && tideData.heights && tideData.heights.length > 0 && (
              <div className="bg-gradient-to-br from-teal-500/10 to-cyan-500/10 rounded-xl p-4 border border-teal-400/30">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-teal-400/60 text-sm">Current Tide</p>
                    <p className="text-3xl font-bold text-teal-400">
                      {tideData.heights[0].height?.toFixed(2) || 'N/A'}m
                    </p>
                  </div>
                  <FaAnchor className="text-4xl text-teal-400/30" />
                </div>
              </div>
            )}
          </div>
        </div>

        {/* Stats Cards */}
        <StatsCards />

        {/* Main Content Grid */}
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6 mb-8">
          {/* Live Map Section */}
          <div className="lg:col-span-2">
            <div className="bg-gradient-to-br from-slate-800/80 to-slate-900/80 rounded-2xl border border-cyan-400/30 p-6 shadow-2xl">
              <div className="flex items-center justify-between mb-4">
                <h2 className="text-2xl font-bold text-cyan-400 flex items-center">
                  <FaGlobe className="mr-3" />
                  Live Port Map
                </h2>
                <div className="flex items-center space-x-2 text-sm text-cyan-400/60">
                  <span className="relative flex h-2 w-2">
                    <span className="animate-ping absolute inline-flex h-full w-full rounded-full bg-green-400 opacity-75"></span>
                    <span className="relative inline-flex rounded-full h-2 w-2 bg-green-500"></span>
                  </span>
                  <span>Real-Time</span>
                </div>
              </div>
              <div className="h-[500px] rounded-xl overflow-hidden border border-cyan-400/20">
                <OpenSeaMap globalSelectedPort={globalSelectedPort} />
              </div>
            </div>
          </div>

          {/* Side Panel */}
          <div className="space-y-6">
            <AIInsights />

            {/* Weather Details */}
            {weather && (
              <div className="bg-gradient-to-br from-slate-800/80 to-slate-900/80 rounded-2xl border border-cyan-400/30 p-6">
                <h3 className="text-xl font-bold text-cyan-400 mb-4">Weather Conditions</h3>
                <div className="space-y-3">
                  <div className="flex justify-between text-sm">
                    <span className="text-cyan-400/60">Condition:</span>
                    <span className="text-cyan-300">{weather.current.condition.text}</span>
                  </div>
                  <div className="flex justify-between text-sm">
                    <span className="text-cyan-400/60">Feels Like:</span>
                    <span className="text-cyan-300">{weather.current.feelslike_c}°C</span>
                  </div>
                  <div className="flex justify-between text-sm">
                    <span className="text-cyan-400/60">Humidity:</span>
                    <span className="text-cyan-300">{weather.current.humidity}%</span>
                  </div>
                  <div className="flex justify-between text-sm">
                    <span className="text-cyan-400/60">Pressure:</span>
                    <span className="text-cyan-300">{weather.current.pressure_mb} mb</span>
                  </div>
                  <div className="flex justify-between text-sm">
                    <span className="text-cyan-400/60">Visibility:</span>
                    <span className="text-cyan-300">{weather.current.vis_km} km</span>
                  </div>
                </div>
              </div>
            )}
          </div>
        </div>

        {/* Port Statistics */}
        <MalaysianPortsStats globalSelectedPort={globalSelectedPort} />
      </div>
    </div>
  );
};

export default CyberHome;

