// Auto-zoom component
function AutoZoom({ vessels, selectedPort }) {
  const map = useMap();

  useEffect(() => {
    if (vessels.length > 0) {
      const bounds = vessels.map(v => [v.latitude, v.longitude]);
      const port = MALAYSIAN_PORTS.find(p => p.id === selectedPort);
      if (port) {
