src/index.css.content:
@tailwind base;
@tailwind components;
@tailwind utilities;

@layer base {
  :root {
    --cyber-navy-dark: #001b2e;
    --cyber-navy-light: #00314d;
    --neon-cyan: #00f3ff;
    --neon-blue: #0099ff;
    --cyber-teal: #00d4ff;
    --glow-cyan: rgba(0, 243, 255, 0.5);
    --glass-bg: rgba(0, 49, 77, 0.3);
    --glass-border: rgba(0, 212, 255, 0.2);
  }

  * {
    scroll-behavior: smooth;
  }

  body {
    margin: 0;
    font-family: 'Montserrat', 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', sans-serif;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    background: linear-gradient(135deg, #001b2e 0%, #00314d 100%);
    background-attachment: fixed;
    color: #e0f3ff;
    overflow-x: hidden;
    position: relative;
  }

  body::before {
    content: '';
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-image: 
      linear-gradient(rgba(0, 243, 255, 0.03) 1px, transparent 1px),
      linear-gradient(90deg, rgba(0, 243, 255, 0.03) 1px, transparent 1px);
    background-size: 50px 50px;
    pointer-events: none;
    z-index: -1;
  }

  body::after {
    content: '';
    position: fixed;
    top: -50%;
    left: -50%;
    width: 200%;
    height: 200%;
    background: radial-gradient(circle, rgba(0, 243, 255, 0.05) 0%, transparent 50%);
    animation: ambientPulse 8s ease-in-out infinite;
    pointer-events: none;
    z-index: -1;
  }

  header {
    z-index: 200 !important;
    position: relative;
  }

  @keyframes ambientPulse {
    0%, 100% { opacity: 0.3; }
    50% { opacity: 0.6; }
  }

  code {
    font-family: source-code-pro, Menlo, Monaco, Consolas, 'Courier New', monospace;
    background: rgba(0, 243, 255, 0.1);
    padding: 2px 6px;
    border-radius: 4px;
  }
}

@layer utilities {
  .glassmorphism {
    background: rgba(0, 49, 77, 0.4);
    backdrop-filter: blur(12px);
    -webkit-backdrop-filter: blur(12px);
    border: 1px solid rgba(0, 212, 255, 0.2);
    box-shadow: 0 8px 32px rgba(0, 0, 0, 0.3);
  }

  .glassmorphism-card {
    background: rgba(0, 27, 46, 0.6);
    backdrop-filter: blur(16px);
    -webkit-backdrop-filter: blur(16px);
    border: 1px solid rgba(0, 243, 255, 0.3);
    box-shadow: 
      0 4px 24px rgba(0, 0, 0, 0.4),
      inset 0 1px 1px rgba(0, 243, 255, 0.2);
  }

  .neon-glow {
    box-shadow: 
      0 0 10px rgba(0, 243, 255, 0.5),
      0 0 20px rgba(0, 243, 255, 0.3),
      0 0 30px rgba(0, 243, 255, 0.2);
  }

  .neon-glow-strong {
    box-shadow: 
      0 0 15px rgba(0, 243, 255, 0.8),
      0 0 30px rgba(0, 243, 255, 0.5),
      0 0 45px rgba(0, 243, 255, 0.3),
      inset 0 0 10px rgba(0, 243, 255, 0.2);
  }

  .neon-text {
    color: #00f3ff;
    text-shadow: 
      0 0 10px rgba(0, 243, 255, 0.8),
      0 0 20px rgba(0, 243, 255, 0.5),
      0 0 30px rgba(0, 243, 255, 0.3);
  }

  .gradient-cyber {
    background: linear-gradient(135deg, #001b2e 0%, #00314d 50%, #004d6d 100%);
  }

  .gradient-neon {
    background: linear-gradient(135deg, #00f3ff 0%, #0099ff 100%);
  }

  .cyber-border {
    border: 1px solid rgba(0, 243, 255, 0.3);
    position: relative;
  }

  .cyber-border::before {
    content: '';
    position: absolute;
    top: -1px;
    left: -1px;
    right: -1px;
    bottom: -1px;
    background: linear-gradient(45deg, transparent, rgba(0, 243, 255, 0.5), transparent);
    border-radius: inherit;
    opacity: 0;
    transition: opacity 0.3s ease;
    pointer-events: none;
  }

  .cyber-border:hover::before {
    opacity: 1;
  }

  .hover-lift-cyber {
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  }

  .hover-lift-cyber:hover {
    transform: translateY(-5px);
    box-shadow: 
      0 12px 40px rgba(0, 0, 0, 0.5),
      0 0 20px rgba(0, 243, 255, 0.4);
  }

  .animate-fade-in-cyber {
    animation: fadeInCyber 0.8s cubic-bezier(0.4, 0, 0.2, 1);
  }

  @keyframes fadeInCyber {
    from {
      opacity: 0;
      transform: translateY(30px);
    }
    to {
      opacity: 1;
      transform: translateY(0);
    }
  }

  .animate-slide-in-cyber {
    animation: slideInCyber 0.6s cubic-bezier(0.4, 0, 0.2, 1);
  }

  @keyframes slideInCyber {
    from {
      opacity: 0;
      transform: translateX(-40px);
    }
    to {
      opacity: 1;
      transform: translateX(0);
    }
  }

  .animate-glow-pulse {
    animation: glowPulse 2s ease-in-out infinite;
  }

  @keyframes glowPulse {
    0%, 100% {
      box-shadow: 
        0 0 10px rgba(0, 243, 255, 0.5),
        0 0 20px rgba(0, 243, 255, 0.3);
    }
    50% {
      box-shadow: 
        0 0 20px rgba(0, 243, 255, 0.8),
        0 0 40px rgba(0, 243, 255, 0.5),
        0 0 60px rgba(0, 243, 255, 0.3);
    }
  }

  .animate-counter {
    animation: counterAppear 1s cubic-bezier(0.4, 0, 0.2, 1);
  }

  @keyframes counterAppear {
    from {
      opacity: 0;
      transform: scale(0.8);
    }
    to {
      opacity: 1;
      transform: scale(1);
    }
  }

  .holographic {
    position: relative;
    overflow: hidden;
  }

  .holographic::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(
      90deg,
      transparent,
      rgba(0, 243, 255, 0.3),
      transparent
    );
    transition: left 0.5s;
  }

  .holographic:hover::before {
    left: 100%;
  }

  .scanline {
    position: relative;
    overflow: hidden;
  }

  .scanline::after {
    content: '';
    position: absolute;
    top: -100%;
    left: 0;
    width: 100%;
    height: 2px;
    background: linear-gradient(
      90deg,
      transparent,
      rgba(0, 243, 255, 0.8),
      transparent
    );
    animation: scanlineMove 3s linear infinite;
  }

  @keyframes scanlineMove {
    0% {
      top: -100%;
    }
    100% {
      top: 100%;
    }
  }
}

src/components/header.js.content:
import React, { useState } from "react";
import { FaWater, FaCircle, FaAnchor, FaChevronDown } from "react-icons/fa";
import { MALAYSIAN_PORTS } from "../constants/malaysianPorts";

const Header = ({ isSidebarOpen, selectedPort, onPortChange }) => {
  const [activeTab, setActiveTab] = useState("live");
  const [isPortDropdownOpen, setIsPortDropdownOpen] = useState(false);

  const currentPort =
    MALAYSIAN_PORTS.find((p) => p.id === selectedPort) || MALAYSIAN_PORTS[0];

  return (
    <header className="fixed top-0 left-0 right-0 z-50 glassmorphism border-b border-cyan-500/30">
      <div className="container mx-auto px-6 py-4">
        <div className="flex items-center justify-between">
          {/* Logo and Brand */}
          <div className="flex items-center space-x-3 group cursor-pointer">
            <div className="relative">
              <div className="w-12 h-12 rounded-lg bg-gradient-to-br from-cyan-500/30 to-blue-600/30 flex items-center justify-center neon-glow-strong border border-cyan-400/50">
                <FaWater className="w-6 h-6 text-cyan-400 animate-pulse" />
              </div>
              <div className="absolute -top-1 -right-1 w-3 h-3">
                <span className="flex h-3 w-3">
                  <span className="animate-ping absolute inline-flex h-full w-full rounded-full bg-cyan-400 opacity-75"></span>
                  <span className="relative inline-flex rounded-full h-3 w-3 bg-cyan-500"></span>
                </span>
              </div>
            </div>

            <div className="flex flex-col">
              <h1 className="text-2xl font-bold tracking-tight">
                <span className="neon-text">CyberPort</span>
              </h1>
              <p className="text-xs text-cyan-300/80 tracking-wider uppercase font-medium">
                Maritime Intelligence
              </p>
            </div>
          </div>

          {/* Navigation Controls */}
          <div className="flex items-center space-x-4">
            {/* Port Selector Dropdown */}
            <div className="relative z-[60]">
              <button
                onClick={() => setIsPortDropdownOpen(!isPortDropdownOpen)}
                className="px-4 py-2 rounded-lg text-sm font-semibold transition-all duration-300 bg-cyan-500/20 text-cyan-400 border border-cyan-500/50 hover:bg-cyan-500/30 flex items-center space-x-2 min-w-[200px] justify-between"
              >
                <div className="flex items-center space-x-2">
                  <FaAnchor className="w-3 h-3" />
                  <span className="truncate">{currentPort.name}</span>
                </div>
                <FaChevronDown
                  className={`w-3 h-3 transition-transform duration-300 ${
                    isPortDropdownOpen ? "rotate-180" : ""
                  }`}
                />
              </button>

              {isPortDropdownOpen && (
                <>
                  {/* Overlay (behind dropdown) */}
                  <div
                    className="fixed inset-0 z-40"
                    onClick={() => setIsPortDropdownOpen(false)}
                  />

                  {/* Dropdown */}
                  <div
                    className="absolute top-full left-0 mt-2 w-full min-w-[300px] glassmorphism rounded-lg border border-cyan-500/30 shadow-2xl max-h-[400px] overflow-y-auto z-50"
                    style={{
                      backgroundColor: "rgba(0, 27, 46, 0.98)",
                      backdropFilter: "blur(12px)",
                    }}
                    onClick={(e) => e.stopPropagation()}
                  >
                    {MALAYSIAN_PORTS.map((port) => (
                      <button
                        key={port.id}
                        onClick={() => {
                          onPortChange(port.id);
                          setIsPortDropdownOpen(false);
                        }}
                        className={`w-full p-3 text-left transition-all duration-300 border-b border-cyan-500/20 last:border-b-0 ${
                          selectedPort === port.id
                            ? "bg-cyan-500/30 text-cyan-300"
                            : "text-cyan-400/80 hover:bg-cyan-500/20 hover:text-cyan-300"
                        }`}
                      >
                        <div className="flex items-center justify-between">
                          <span className="font-medium text-sm">
                            {port.name}
                          </span>
                          {selectedPort === port.id && (
                            <FaCircle className="w-2 h-2 text-cyan-400 animate-pulse" />
                          )}
                        </div>
                      </button>
                    ))}
                  </div>
                </>
              )}
            </div>

            {/* Tab Buttons */}
            <div className="flex items-center space-x-2">
              <button
                onClick={() => setActiveTab("live")}
                className={`px-4 py-2 rounded-lg text-sm font-semibold transition-all duration-300 ${
                  activeTab === "live"
                    ? "bg-cyan-500/20 text-cyan-400 neon-glow border border-cyan-500/50"
                    : "text-cyan-300/60 hover:text-cyan-400 hover:bg-cyan-500/10 border border-transparent"
                }`}
              >
                <div className="flex items-center space-x-2">
                  <FaCircle className="w-2 h-2 animate-pulse" />
                  <span>LIVE</span>
                </div>
              </button>

              <button
                onClick={() => setActiveTab("reports")}
                className={`px-4 py-2 rounded-lg text-sm font-semibold transition-all duration-300 ${
                  activeTab === "reports"
                    ? "bg-cyan-500/20 text-cyan-400 neon-glow border border-cyan-500/50"
                    : "text-cyan-300/60 hover:text-cyan-400 hover:bg-cyan-500/10 border border-transparent"
                }`}
              >
                REPORTS
              </button>

              <button
                onClick={() => setActiveTab("settings")}
                className={`px-4 py-2 rounded-lg text-sm font-semibold transition-all duration-300 ${
                  activeTab === "settings"
                    ? "bg-cyan-500/20 text-cyan-400 neon-glow border border-cyan-500/50"
                    : "text-cyan-300/60 hover:text-cyan-400 hover:bg-cyan-500/10 border border-transparent"
                }`}
              >
                SETTINGS
              </button>
            </div>

            {/* System Status */}
            <div className="px-4 py-2 glassmorphism rounded-lg border border-cyan-500/30">
              <div className="flex items-center space-x-2 text-sm">
                <span className="text-cyan-300/60">Status:</span>
                <span className="text-green-400 font-semibold flex items-center">
                  <FaCircle className="w-2 h-2 mr-1.5 animate-pulse" />
                  OPERATIONAL
                </span>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Decorative Lines */}
      <div className="absolute bottom-0 left-0 right-0 h-px bg-gradient-to-r from-transparent via-cyan-500 to-transparent opacity-50"></div>
      <div className="absolute top-0 left-0 right-0 h-px bg-gradient-to-r from-transparent via-cyan-500/30 to-transparent"></div>
    </header>
  );
};

export default Header;

